.time-slot {
  position: relative;
  margin: 1.5em auto;
  max-width: 320px; }

.time-slot--taken .time-slot__input {
  display: none; }
  .time-slot--taken .time-slot__input + .time-slot__label {
    cursor: default;
    pointer-events: none; }
    .time-slot--taken .time-slot__input + .time-slot__label:before {
      background: #f0f0f0; }

.time-slot__input {
  display: block;
  position: absolute;
  top: 4px;
  left: 4px;
  z-index: 100;
  padding: 0 0.55em;
  width: calc(100% - 12px);
  height: calc(100% - 8px);
  background: #f0f0f0;
  opacity: 0;
  transform: scale3d(1, 0, 1);
  transform-origin: 50% 100%;
  transition: opacity 0.3s, transform 0.3s; }
  .time-slot__input:focus {
    opacity: 1;
    transform: scale3d(1, 1, 1); }
    .time-slot__input:focus + .time-slot__label {
      cursor: default;
      pointer-events: none; }
      .time-slot__input:focus + .time-slot__label .time-slot__label-content {
        transform: translate3d(0, -2.15em, 0) scale3d(0.8, 0.8, 1) translateZ(1px); }
      .time-slot__input:focus + .time-slot__label:before {
        transform: scale3d(1, 1.5, 1); }

.time-slot__label {
  display: inline-block;
  width: 100%;
  text-align: left;
  padding: 1em;
  cursor: text;
  font-size: 1.2em; }
  .time-slot__label:before {
    content: '';
